#include <sstream>#include <string>#include "includes/TestException.hpp"namespace cdt {TestException::TestException(const std::string& file, int line, const std::string& msg) :	m_msg(msg), m_file(file), m_line(line) {};TestException::TestException(const std::string& file, int line) :	m_file(file), m_line(line) {		m_msg = "Exception occured";	};TestException::TestException(const std::string& msg) : m_msg(msg), m_file(""), m_line(0) {};TestException::~TestException() throw() {};const char* TestException::what() const throw() {	return m_msg.c_str();}const char* TestException::formatedMsg() const {	std::stringstream ss;	ss	<< m_file << ":" << m_line << " - " << m_msg;	return ss.str().c_str();}const std::string& TestException::getFile() const {	return m_file;}const int TestException::getLine() const {	return m_line;}} // namespace cdt